import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useCart } from '../context/CartContext';

const CartScreen = () => {
  const { cartItems, removeFromCart, getCartTotal, clearCart } = useCart();
  const navigate = useNavigate();

  const checkoutHandler = () => {
    navigate('/login?redirect=/shipping');
  };

  if (cartItems.length === 0) {
    return (
      <div style={{ padding: '2rem', textAlign: 'center' }}>
        <h2>Your Cart is Empty</h2>
        <p>Add some products to your cart!</p>
        <Link to="/" className="btn btn-primary">
          Continue Shopping
        </Link>
      </div>
    );
  }

  return (
    <div style={{ padding: '2rem' }}>
      <h1>Shopping Cart</h1>
      <div style={{ display: 'flex', gap: '2rem', flexWrap: 'wrap' }}>
        {/* Cart Items */}
        <div style={{ flex: 2 }}>
          {cartItems.map((item) => (
            <div
              key={item.product}
              style={{
                display: 'flex',
                gap: '1rem',
                padding: '1rem',
                border: '1px solid #ddd',
                borderRadius: '8px',
                marginBottom: '1rem',
                alignItems: 'center'
              }}
            >
              <img
                src={`https://via.placeholder.com/100x100/007bff/ffffff?text=${encodeURIComponent(item.name)}`}
                alt={item.name}
                style={{ width: '100px', height: '100px', objectFit: 'cover', borderRadius: '4px' }}
              />
              
              <div style={{ flex: 1 }}>
                <Link to={`/product/${item.product}`} style={{ textDecoration: 'none' }}>
                  <h4 style={{ margin: 0, color: '#333' }}>{item.name}</h4>
                </Link>
                <p style={{ margin: '0.5rem 0', color: '#666' }}>${item.price}</p>
              </div>
              
              <div style={{ textAlign: 'center' }}>
                <p style={{ margin: 0, fontWeight: 'bold' }}>Qty: {item.quantity}</p>
                <p style={{ margin: '0.5rem 0', fontWeight: 'bold' }}>
                  ${(item.quantity * item.price).toFixed(2)}
                </p>
              </div>
              
              <button
                onClick={() => removeFromCart(item.product)}
                style={{
                  backgroundColor: '#dc3545',
                  color: 'white',
                  border: 'none',
                  padding: '0.5rem 1rem',
                  borderRadius: '4px',
                  cursor: 'pointer'
                }}
              >
                Remove
              </button>
            </div>
          ))}
          
          <button
            onClick={clearCart}
            style={{
              backgroundColor: '#6c757d',
              color: 'white',
              border: 'none',
              padding: '0.75rem 1.5rem',
              borderRadius: '4px',
              cursor: 'pointer',
              marginTop: '1rem'
            }}
          >
            Clear Cart
          </button>
        </div>
        
        {/* Order Summary */}
        <div style={{ 
          flex: 1,
          border: '1px solid #ddd',
          borderRadius: '8px',
          padding: '1.5rem',
          height: 'fit-content'
        }}>
          <h3>Order Summary</h3>
          <div style={{ marginBottom: '1rem' }}>
            <strong>Total Items: {cartItems.reduce((acc, item) => acc + item.quantity, 0)}</strong>
          </div>
          <div style={{ marginBottom: '1rem' }}>
            <strong>Subtotal: ${getCartTotal().toFixed(2)}</strong>
          </div>
          <button
            onClick={checkoutHandler}
            style={{
              width: '100%',
              padding: '0.75rem',
              backgroundColor: '#28a745',
              color: 'white',
              border: 'none',
              borderRadius: '4px',
              cursor: 'pointer',
              fontSize: '1rem',
              fontWeight: 'bold'
            }}
          >
            Proceed to Checkout
          </button>
        </div>
      </div>
    </div>
  );
};

export default CartScreen;
