import React, { useState, useEffect } from 'react';
import { Row, Col, Alert, Spinner, Button } from 'react-bootstrap';
import axios from 'axios';
import Product from '../components/Product';

const HomeScreen = () => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  const fetchProducts = async () => {
    try {
      setLoading(true);
      setError('');
      console.log('Fetching products from backend...');
      
      const { data } = await axios.get('http://localhost:8001/api/products');
      console.log('Products received:', data);
      
      setProducts(data);
      setLoading(false);
    } catch (error) {
      console.error('Fetch error:', error);
      setError(`Backend connection failed: ${error.message}. Make sure backend is running on port 8001.`);
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchProducts();
  }, []);

  return (
    <>
      <h1>Latest Products</h1>
      {loading ? (
        <div className="text-center">
          <Spinner animation="border" role="status">
            <span className="visually-hidden">Loading...</span>
          </Spinner>
          <p>Loading products from backend...</p>
        </div>
      ) : error ? (
        <Alert variant="danger">
          <Alert.Heading>Connection Error</Alert.Heading>
          <p>{error}</p>
          <hr />
          <div className="d-flex justify-content-end">
            <Button onClick={fetchProducts} variant="outline-danger">
              Try Again
            </Button>
          </div>
        </Alert>
      ) : (
        <>
          <p>Showing {products.length} products</p>
          <Row>
            {products.map((product) => (
              <Col key={product._id} sm={12} md={6} lg={4} xl={3}>
                <Product product={product} />
              </Col>
            ))}
          </Row>
        </>
      )}
    </>
  );
};

export default HomeScreen;
