import React, { useState, useEffect } from 'react';
import { Link, useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { useCart } from '../context/CartContext';
import Rating from '../components/Rating';

const ProductScreen = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(true);
  const [quantity, setQuantity] = useState(1);
  
  const { addToCart } = useCart();

  useEffect(() => {
    const fetchProduct = async () => {
      try {
        const { data } = await axios.get(`http://localhost:8001/api/products/${id}`);
        setProduct(data);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching product:', error);
        setLoading(false);
      }
    };

    fetchProduct();
  }, [id]);

  const addToCartHandler = () => {
    addToCart(product, quantity);
    navigate('/cart'); // Redirect to cart page after adding
  };

  if (loading) {
    return (
      <div style={{ padding: '2rem' }}>
        <Link to="/" className="btn btn-light mb-3">
          ← Go Back
        </Link>
        <h2>Loading product...</h2>
      </div>
    );
  }

  if (!product) {
    return (
      <div style={{ padding: '2rem' }}>
        <Link to="/" className="btn btn-light mb-3">
          ← Go Back
        </Link>
        <h2>Product not found</h2>
      </div>
    );
  }

  return (
    <div style={{ padding: '2rem' }}>
      <Link to="/" className="btn btn-light mb-3">
        ← Go Back
      </Link>
      
      <div style={{ display: 'flex', gap: '2rem', flexWrap: 'wrap' }}>
        {/* Product Image */}
        <div>
          <img 
            src={`https://via.placeholder.com/400x400/007bff/ffffff?text=${encodeURIComponent(product.name)}`}
            alt={product.name}
            style={{ width: '400px', height: '400px', objectFit: 'cover', borderRadius: '8px' }}
          />
        </div>
        
        {/* Product Details */}
        <div style={{ flex: 1 }}>
          <h1>{product.name}</h1>
          
          <div className="my-2">
            <Rating
              value={product.rating}
              text={`${product.numReviews} reviews`}
            />
          </div>
          
          <p><strong>Price:</strong> ${product.price}</p>
          <p><strong>Description:</strong> {product.description}</p>
          <p><strong>Brand:</strong> {product.brand}</p>
          <p><strong>Category:</strong> {product.category}</p>
          <p>
            <strong>Status:</strong> 
            <span style={{ color: product.countInStock > 0 ? 'green' : 'red', marginLeft: '0.5rem' }}>
              {product.countInStock > 0 ? 'In Stock' : 'Out of Stock'}
            </span>
          </p>
        </div>
        
        {/* Add to Cart Section */}
        <div style={{ 
          border: '1px solid #ddd', 
          borderRadius: '8px', 
          padding: '1.5rem',
          minWidth: '300px',
          height: 'fit-content'
        }}>
          <h3>Add to Cart</h3>
          <div style={{ marginBottom: '1rem' }}>
            <strong>Price: ${product.price}</strong>
          </div>
          <div style={{ marginBottom: '1rem' }}>
            <strong>Status: </strong>
            <span style={{ color: product.countInStock > 0 ? 'green' : 'red' }}>
              {product.countInStock > 0 ? 'In Stock' : 'Out of Stock'}
            </span>
          </div>
          
          {product.countInStock > 0 && (
            <div style={{ marginBottom: '1rem' }}>
              <label><strong>Quantity:</strong></label>
              <select 
                style={{ 
                  display: 'block', 
                  width: '100%', 
                  padding: '0.5rem',
                  marginTop: '0.5rem',
                  borderRadius: '4px',
                  border: '1px solid #ccc'
                }}
                value={quantity}
                onChange={(e) => setQuantity(Number(e.target.value))}
              >
                {[...Array(Math.min(product.countInStock, 10)).keys()].map(x => (
                  <option key={x + 1} value={x + 1}>
                    {x + 1}
                  </option>
                ))}
              </select>
            </div>
          )}
          
          <button
            style={{
              width: '100%',
              padding: '0.75rem',
              backgroundColor: product.countInStock > 0 ? '#007bff' : '#6c757d',
              color: 'white',
              border: 'none',
              borderRadius: '4px',
              cursor: product.countInStock > 0 ? 'pointer' : 'not-allowed',
              fontSize: '1rem',
              fontWeight: 'bold'
            }}
            disabled={product.countInStock === 0 ()}
            onClick={addToCartHandler}
          >
            {product.countInStock > 0 ? 'Add to Cart' : 'Out of Stock'}
          </button>
        </div>
      </div>
    </div>
  );
};

export default ProductScreen;
