import React, { useState, useEffect } from 'react';
import { Link, useParams } from 'react-router-dom';
import { Row, Col, Image, ListGroup, Card, Button, Form } from 'react-bootstrap';
import axios from 'axios';
import Rating from '../components/Rating';

const ProductScreen = () => {
  const { id } = useParams();
  const [product, setProduct] = useState({});
  const [loading, setLoading] = useState(true);
  const [quantity, setQuantity] = useState(1);

  useEffect(() => {
    const fetchProduct = async () => {
      try {
        const { data } = await axios.get(`http://localhost:8001/api/products/${id}`);
        setProduct(data);
        setLoading(false);
      } catch (error) {
        console.error('Error fetching product:', error);
        setLoading(false);
      }
    };

    fetchProduct();
  }, [id]);

  const addToCartHandler = () => {
    console.log(`Add to cart: ${quantity} of ${product.name}`);
    // We'll implement cart functionality in the next step
    alert(`Added ${quantity} ${product.name} to cart!`);
  };

  if (loading) {
    return (
      <div className="text-center py-5">
        <h3>Loading product details...</h3>
      </div>
    );
  }

  return (
    <>
      <Link className="btn btn-light my-3" to="/">
        ‚Üê Go Back
      </Link>
      
      <Row>
        {/* Product Image */}
        <Col md={6}>
          <Image 
            src={`https://via.placeholder.com/500x500/007bff/ffffff?text=${encodeURIComponent(product.name)}`} 
            alt={product.name} 
            fluid 
            className="rounded"
          />
        </Col>
        
        {/* Product Details */}
        <Col md={3}>
          <ListGroup variant="flush">
            <ListGroup.Item>
              <h2>{product.name}</h2>
            </ListGroup.Item>
            
            <ListGroup.Item>
              <Rating
                value={product.rating}
                text={`${product.numReviews} reviews`}
              />
            </ListGroup.Item>
            
            <ListGroup.Item>
              <strong>Price:</strong> ${product.price}
            </ListGroup.Item>
            
            <ListGroup.Item>
              <strong>Description:</strong> {product.description}
            </ListGroup.Item>
            
            <ListGroup.Item>
              <strong>Brand:</strong> {product.brand}
            </ListGroup.Item>
            
            <ListGroup.Item>
              <strong>Category:</strong> {product.category}
            </ListGroup.Item>
          </ListGroup>
        </Col>
        
        {/* Add to Cart Section */}
        <Col md={3}>
          <Card>
            <ListGroup variant="flush">
              <ListGroup.Item>
                <Row>
                  <Col>Price:</Col>
                  <Col>
                    <strong>${product.price}</strong>
                  </Col>
                </Row>
              </ListGroup.Item>
              
              <ListGroup.Item>
                <Row>
                  <Col>Status:</Col>
                  <Col>
                    {product.countInStock > 0 ? (
                      <span className="text-success">In Stock</span>
                    ) : (
                      <span className="text-danger">Out of Stock</span>
                    )}
                  </Col>
                </Row>
              </ListGroup.Item>
              
              {/* Quantity Selector - Only show if product is in stock */}
              {product.countInStock > 0 && (
                <ListGroup.Item>
                  <Row>
                    <Col>Quantity:</Col>
                    <Col>
                      <Form.Control
                        as="select"
                        value={quantity}
                        onChange={(e) => setQuantity(Number(e.target.value))}
                      >
                        {[...Array(Math.min(product.countInStock, 10)).keys()].map((x) => (
                          <option key={x + 1} value={x + 1}>
                            {x + 1}
                          </option>
                        ))}
                      </Form.Control>
                    </Col>
                  </Row>
                </ListGroup.Item>
              )}
              
              <ListGroup.Item>
                <Button
                  onClick={addToCartHandler}
                  className="btn-block w-100"
                  type="button"
                  disabled={product.countInStock === 0}
                  variant={product.countInStock > 0 ? "primary" : "secondary"}
                >
                  {product.countInStock > 0 ? 'Add to Cart' : 'Out of Stock'}
                </Button>
              </ListGroup.Item>
            </ListGroup>
          </Card>
        </Col>
      </Row>
    </>
  );
};

export default ProductScreen;
